import { useProductsContext } from "../hooks/useProductsContext";
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

//components
import ProductDetails from "../components/ProductDetails"

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState(""); // State to store search term
  const [categoryFilter, setCategoryFilter] = useState(""); // State to store category filter
  const [selectedSupplier, setSelectedSupplier] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]); // State to store filtered products

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products');
      const json = await response.json();

      const supplier = [...new Set(products.map((product) => product.supplier))];

      if (response.ok) {
        dispatch({ type: 'SET_PRODUCTS', payload: json });
        // Inside your useEffect that fetches products
        setFilteredProducts(json); // initialize the filterProduct with the products
      }
    };

    fetchProducts();
  }, [dispatch]);

  useEffect(() => {
    if (products === null) {
      // If products is null, return
      return;
    }

    // Filter products based on all filter criteria
    const filtered = products.filter((product) => {
      const fieldsToSearch = [
        product.name,
        product.category,
        product.supplier,
        product.description,
        product.quantity.toString(),
        product.reOrderLevel.toString(),
        product.price.toString(),
      ];

      const searchString = fieldsToSearch.join(" ").toLowerCase();
      const searchTermMatches = searchString.includes(searchTerm.toLowerCase());
      const categoryMatch = categoryFilter === "" || product.category.toLowerCase() === categoryFilter.toLowerCase();
      const supplierMatch = selectedSupplier === "" || product.supplier.toLowerCase().includes(selectedSupplier.toLowerCase());

      return searchTermMatches && categoryMatch && supplierMatch;
    });

    setFilteredProducts(filtered);
  }, [searchTerm, categoryFilter, selectedSupplier, products]);

  return (
    <div className="home">
      <div className="top-bar">
        <Link to="/NewProduct">
          <button className="createb">Add</button>
        </Link>

        <div className="filter-controls">
          <label>Category:</label>
          <input
            type="text"
            placeholder="Filter by Category"
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
          />

          <label>Supplier:</label>
          <select
            value={selectedSupplier}
            onChange={(e) => setSelectedSupplier(e.target.value)}
          >
            <option value="">All Suppliers</option>
            {suppliers.map((supplier) => (
              <option key={supplier} value={supplier}>
                {supplier}
              </option>
            ))}
          </select>

        </div>

        <div className="search-bar">
          <span className="search-icon">search</span>
          <input
            type="text"
            placeholder="Search Products"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <table className="product-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Supplier</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Re-OrderLevel</th>
            <th>Price</th>
            <th>Created</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts && filteredProducts.map((product) => (
            <ProductDetails key={product._id} product={product} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
