import React from "react";
import jsPDF from "jspdf";
import "jspdf-autotable";
import { formatDistanceToNow } from "date-fns";

const ExportToPDF = ({ data }) => {
  const exportToPDF = () => {
    const doc = new jsPDF();

    // Define the columns and rows of the table
    const columns = [
      "ID",
      "Product Name",
      "Category",
      "Supplier",
      "Description",
      "Quantity",
      "Re-Order Level",
      "Price",
      "Created",
    ];
    const rows = data.map((product, index) => [
      product._id.slice(-5),
      product.name,
      product.category,
      product.supplier,
      product.description,
      product.quantity,
      product.reOrderLevel,
      `Rs ${product.price}`,
      formatDistanceToNow(new Date(product.createdAt), { addSuffix: true }),
    ]);

    // Set the table position and size
    const margin = 5;
    const pageWidth = doc.internal.pageSize.width - 2 * margin;
    doc.setFontSize(15);
    doc.text("Product Table", margin, 15);

    const tableStyles = {
      theme: "striped",
      headStyles: { fillColor: [41, 128, 185], textColor: 255 },
      columnStyles: { 0: { cellWidth: 30 } },
      tableLineWidth: 0.1, // Specify the width of the lines between cells
    };

    doc.autoTable({
      startY: 20,
      head: [columns],
      body: rows,
      ...tableStyles,
    });

    // Save the PDF with a name
    doc.save("product_List.pdf");
  };

  return (
    <div className="downloadb">

      <button onClick={exportToPDF} type="button" class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">
    DownLoad File
    <svg class="w-3.5 h-3.5 ml-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
    <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9"/>
  </svg>
</button>
    </div>
  );
};

export default ExportToPDF;
