import { useProductsContext } from "../hooks/useProductsContext";
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';
import jsPDF from 'jspdf'

//components
import ProductDetails from "../components/ProductDetails"

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState(""); // State to store search term
  const [selectedSupplier, setSelectedSupplier] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]); // State to store filtered products
  const [suppliers, setSuppliers] = useState([]); // State to store unique suppliers


  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products');
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_PRODUCTS', payload: json });
        setFilteredProducts(json); // initiallize the filterProduct with the products
        const uniqueSuppliers = [...new Set(json.map((product) => product.supplier))]; //array tp store the unique suppliers
        setSuppliers(uniqueSuppliers);
      }
    };

    fetchProducts();
  }, [dispatch]);

  useEffect(() => {

    if (products === null) {
      // If products is null
      return;
    }
    // Filter products based on the search term when it changes
    const filtered = products.filter((product) => {
      const matchesSearch = Object.values(product).some((field) => {
        if (typeof field === 'string') {
          return field.toLowerCase().includes(searchTerm.toLowerCase());
        }
        return false;
      });
      const matchesSupplier = selectedSupplier === "" || product.supplier === selectedSupplier;

      return matchesSearch && matchesSupplier;
    });

    setFilteredProducts(filtered);
  }, [searchTerm, selectedSupplier, products]);


  exportProductList = () => {
    console.log('Exporting Delivery PDF')
    const unit = 'pt'
    const size = 'A4'
    const orientation = 'landscape'
    const marginLeft = 40
    const doc = new jsPDF(orientation, unit, size)
    const title = 'Product List'
    const headers = [['ID', 'product name', 'category', 'Supplier', 'description', 'Quantity', 'Re-OrderLevel', 'Price', 'Created']]
    const products = this.state.product.map((product) => [
      product.name.substring(0, 10),
      product.category,
      product.supplier,
      product.description,
      product.quantity,
      product.reOrderLevel,
      product.Price + '.00',
    ])
    let content = {
      startY: 50,
      head: headers,
      body: product,
    }
    doc.setFontSize(20)
    doc.text(title, marginLeft, 40)
    require('jspdf-autotable')
    doc.autoTable(content)
    doc.save('Product Report.pdf')
  }

  return (
    <div className="home">
      <div className="top-bar">
        <Link to="/NewProduct">
          <button className="createb">Add</button>
        </Link>

        <button
          className='flex text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800'
          onClick={() => this.exportProductList()}
        >
          <div className=''>
            <svg
              className='h-5 w-5 mr-2'
              fill='none'
              viewBox='0 0 24 24'
              stroke='currentColor'
            >
              <path
                strokeLinecap='round'
                strokeLinejoin='round'
                d='M19.5 14.25v-2.625a3.375 3.375 0 00-3.375-3.375h-1.5A1.125 1.125 0 0113.5 7.125v-1.5a3.375 3.375 0 00-3.375-3.375H8.25m2.25 0H5.625c-.621 0-1.125.504-1.125 1.125v17.25c0 .621.504 1.125 1.125 1.125h12.75c.621 0 1.125-.504 1.125-1.125V11.25a9 9 0 00-9-9z'
              />
            </svg>
          </div>
          <div className=''>Download Report</div>
        </button>

        <div className="search-bar">
          <span className="search-icon">search</span>
          <input
            type="text"
            placeholder="Search Products"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-bar">
          <span>Filter by Supplier:</span>
          <select
            value={selectedSupplier}
            onChange={(e) => setSelectedSupplier(e.target.value)}
          >
            <option value="">All Suppliers</option>
            {suppliers.map((supplier) => (
              <option key={supplier} value={supplier}>
                {supplier}
              </option>
            ))}
          </select>
        </div>
      </div>


      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Product Name</th>
            <th>Category</th>
            <th>Supplier</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Re-OrderLevel</th>
            <th>Price</th>
            <th>Created</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts && filteredProducts.map((product) => (
            <ProductDetails key={product._id} product={product} />
          ))}
        </tbody>
      </table>

    </div>
  );
}

export default Home;
