import { useProductsContext } from "../hooks/useProductsContext";
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

//components
import ProductDetails from "../components/ProductDetails"

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState(""); // State to store search term
  const [categoryFilter, setCategoryFilter] = useState(""); // State to store category filter
  const [supplierFilter, setSupplierFilter] = useState(""); // State to store supplier filter
  const [filteredProducts, setFilteredProducts] = useState([]); // State to store filtered products

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products');
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_PRODUCTS', payload: json });
        setFilteredProducts(json); // initiallize the filterProduct with the products
      }
    };

    fetchProducts();
  }, [dispatch]);

  useEffect(() => {
    if (products === null) {
      // If products is null, return
      return;
    }

    // Filter products based on the search term
    const filtered = products.filter((product) => {
      return Object.values(product).some((field) => {
        if (typeof field === 'string') {
          return field.toLowerCase().includes(searchTerm.toLowerCase());
        }
        return false;
      });
    });

    setFilteredProducts(filtered);
  }, [searchTerm, products]);

  useEffect(() => {
    if (products === null) {
      // If products is null, return
      return;
    }

    // Filter products based on the category
    const filtered = products.filter((product) => {
      return categoryFilter === "" || product.category.toLowerCase() === categoryFilter.toLowerCase();
    });

    setFilteredProducts(filtered);
  }, [categoryFilter, products]);

  // Filter products based on the supplier
  useEffect(() => {
    if (products === null) {
      // If products is null, return
      return;
    }

    // Filter products based on the supplier
    const filtered = products.filter((product) => {
      return supplierFilter === "" || product.supplier.toLowerCase() === supplierFilter.toLowerCase();
    });

    setFilteredProducts(filtered);
  }, [supplierFilter, products]);

  return (
    <div className="home">
      <div className="top-bar">
        <Link to="/NewProduct">
          <button className="createb">Add</button>
        </Link>

        <div className="filter-controls">
          <label>Category:</label>
          <input
            type="text"
            placeholder="Filter by Category"
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
          />

          <label>Supplier:</label>
          <input
            type="text"
            placeholder="Filter by Supplier"
            value={supplierFilter}
            onChange={(e) => setSupplierFilter(e.target.value)}
          />
        </div>

        <div className="search-bar">
          <span className="search-icon">search</span>
          <input
            type="text"
            placeholder="Search Products"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <table className="product-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Supplier</th>
            <th>Description</th>
            <th>Quantity</th>
            <th>Re-OrderLevel</th>
            <th>Price</th>
            <th>Created</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredProducts && filteredProducts.map((product) => (
            <ProductDetails key={product._id} product={product} />
          ))}
        </tbody>
      </table>

    </div>
  );
}

export default Home;
