import { useProductsContext } from '../hooks/useProductsContext'

//date fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const ProductDetails = ({ product }) => {
    const { dispatch } = useProductsContext()

    const handleClickDelete = async() =>{
        const response = await fetch('/api/products/'+ product._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if(response.ok){
            dispatch({type: 'DELETE_WORKOUT' , payload: json})
        }
    }
    const handleClickUpdate = async() =>{
        const response = await fetch('/api/products/'+ product._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if(response.ok){
            dispatch({type: 'DELETE_WORKOUT' , payload: json})
        }
    }
    return(
        <div className="product-details">
            <h4>{product.name}</h4>
            <p><strong>category : </strong>{product.category} </p>
            <p><strong>supplier: </strong>{product.supplier} </p>
            <p><strong>description: </strong>{product.description} </p>
            <p><strong>price: </strong>{product.price} </p>

            <p>{formatDistanceToNow(new Date(product.createdAt),{ addSuffix: true})}</p>
            <span className="material-symbols-outlined" onClick = {handleClickDelete} >delete</span>
            <span className="material-symbols-outlined" onClick= {handleClickUpdate}>edit</span>
        </div>
    )
} 


export default ProductDetails
