import { useState } from "react";
import { useProductsContext } from "../hooks/useProductsContext";
import { useNavigate } from "react-router-dom";

// import { useEffect } from 'react';

const ProductForm = () => {
  const { dispatch } = useProductsContext();
  const navigate = useNavigate(); // Hook for programmatic navigation

  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [supplier, setSupplier] = useState("");
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState("");
  const [reOrderLevel, setReOrderLevel] = useState("");
  const [price, setPrice] = useState("");

  //for error checking
  const [error, setError] = useState({
    name: "",
    category: "",
    supplier: "",
    description: "",
    quantity: "",
    reOrderLevel: "",
    price: ""
  });

  const [emptyFields, setEmptyFields] = useState([]);

  // const [suppliersList, setSuppliersList] = useState([]);

  // useEffect(() => {
  //   // Fetch the list of suppliers from your API
  //   const fetchSuppliers = async () => {
  //     try {
  //       const response = await fetch('/api/suppliers');                      //when supplier data is added
  //       const data = await response.json();

  //       if (response.ok) {
  //         // Extract the list of supplier names
  //         const supplierNames = data.map((supplier) => supplier.name);
  //         setSuppliersList(supplierNames);
  //       } else {
  //         console.error('Failed to fetch suppliers');
  //       }
  //     } catch (error) {
  //       console.error('Error fetching suppliers:', error);
  //     }
  //   };

  //   fetchSuppliers();
  // }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = {}; // Temporary object to store errors

    if (!/^[a-zA-Z][a-zA-Z0-9\s]*$/.test(name)) {
        newErrors.name ="Name must start with a letter and can only contain letters, numbers, and spaces."
    }
    if (name.length <= 2) {
      newErrors.name = "Name must be greater than 2 letters."
    }

    if (!/^[a-zA-Z][a-zA-Z0-9\s]*$/.test(description)) {
      newErrors.description ="description must start with a letter and can only contain letters, numbers, and spaces"
    }

    const quantityVal = parseInt(quantity);
    if (quantityVal <= 0) {
      newErrors.quantity ="Quantity must be a valid number greater than 0"
    }
    const reOrderLevelValue = parseFloat(reOrderLevel);
    if (reOrderLevelValue <= 0 || reOrderLevelValue >= parseFloat(quantity)) {
      newErrors.reOrderLevel=  "Re-Order Level must be a valid number greater than 0 and less than Quantity."
    }
    const priceVal = parseFloat(price);
    if (priceVal <= 0) {
      newErrors.price ="Price must be a valid number greater than 0"
    }

    setError(newErrors)

    if (Object.keys(newErrors).length > 0) {
      // If there are errors, update the error state and return
      setError(newErrors);
      return;
    }

    // Clear errors for valid inputs
    setError({});

    const product = {
      name,
      category,
      supplier,
      description,
      quantity,
      reOrderLevel,
      price,
    };

    const response = await fetch("/api/products", {
      method: "POST",
      body: JSON.stringify(product),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const json = await response.json();

    if (!response.ok) {
      const data = await response.json();
      if (data.error) {
        // Handle server-side errors
        setError(data.error);
      }
      // setError({ error: json.error });
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setName("");
      setCategory("");
      setSupplier("");
      setDescription("");
      setQuantity("");
      setReOrderLevel("");
      setPrice("");
      setError(null);
      setEmptyFields([]);
      console.log("new product added", json);
      dispatch({ type: "CREATE_PRODUCT", payload: json });

      // Redirect to the home page
      navigate("/");
    }
  };

  return (
  <div className="form-container">
    <form className="format" onSubmit={handleSubmit}>
      <h3>Add a new Product</h3>

      <div className="form-row">
        <div className="form-column">

          <label>Product Name :</label>
          <input
            type="text"
            onChange={(e) => setName(e.target.value)}
            value={name}
            className={emptyFields.includes("name") || (error && error.name) ? "error" : ""}
          />

          <label>Category :</label>
          <input
            type="text"
            onChange={(e) => setCategory(e.target.value)}
            value={category}
            className={emptyFields.includes("category") || (error && error.category) ? "error" : ""}
          />

          <label>Description :</label>
          <input
            type="text"
            onChange={(e) => setDescription(e.target.value)}
            value={description}
            className={emptyFields.includes("description") || (error && error.description) ? "error" : ""}
          />
        </div>

        <div className="form-column">
          <label>Supplier :</label>
          <input
            type="text"
            onChange={(e) => setSupplier(e.target.value)}
            value={supplier}
            className={emptyFields.includes("supplier") || (error && error.supplier("supplier")) ? "error" : ""}
          />

          <label>Quantity:</label>
          <input
            type="number"
            onChange={(e) => setQuantity(e.target.value)}
            value={quantity}
            className={
              emptyFields.includes("quantity") || (error && error.quantity("quantity")) ? "error" : ""
            }
          />

          <label>Re-OrderLevel:</label>
          <input
            type="number"
            onChange={(e) => setReOrderLevel(e.target.value)}
            value={reOrderLevel}
            className={
              emptyFields.includes("reOrderLevel") || (error && error.reOrderLevel("reOrderLevel")) ? "error" : ""
            }
          />

          <label>Price:</label>
          <input
            type="number"
            onChange={(e) => setPrice(e.target.value)}
            value={price}
            className={emptyFields.includes("price") || (error && error.price("price")) ? "error" : ""}
          />
        </div>
      </div>

      <button>Add Product</button>
      {error && <div className="error">{error}</div>}
    </form>
  </div>
  );
};

export default ProductForm;
