import React from 'react';
import { useCloudinaryContext } from 'cloudinary-react'; // Import Cloudinary context


const ImageUpload = ({ onUpload }) => {
  const { openUploadWidget } = useCloudinaryContext();

  const handleUpload = () => {
    // Open the Cloudinary upload widget
    openUploadWidget(
      {
        cloudName: 'dz6bbtwhd',
        uploadPreset: 'ml_default',
        sources: ['local', 'url'],
        cropping: true,
        multiple: false,
        resourceType: 'image',
        // Add any other options you need
      },
      (error, result) => {
        if (!error && result && result.event === 'success') {
          const uploadedImage = result.info;
          onUpload(uploadedImage.url); // Send the image URL to the parent component
        }
      }
    );
  };

  return (
    <div className="image-upload">
      <button onClick={handleUpload}>Upload Image</button>
      <Image
        cloudName="dz6bbtwhd"
        publicId="sample" // Replace with the public ID of the uploaded image
        width="300"
        crop="scale"
      />
    </div>
  );
};

export default ImageUpload;
