import { useState } from 'react';
import { useProductsContext } from '../hooks/useProductsContext';
import { useNavigate } from 'react-router-dom';

const ProductForm = () => {
  const { dispatch } = useProductsContext();
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [supplier, setSupplier] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [reOrderLevel, setReOrderLevel] = useState('');
  const [price, setPrice] = useState('');

  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validations
    if (!name || !category || !supplier || !description || !quantity || !reOrderLevel || !price) {
      setError('All fields are required');
      return;
    }

    if (parseInt(reOrderLevel) <= 0 || parseInt(quantity) <= 0) {
      setError('Re-Order Level and Quantity must be greater than 0');
      return;
    }

    // Proceed with form submission logic.

    const product = { name, category, supplier, description, quantity, reOrderLevel, price };

    const response = await fetch('/api/products', {
      method: 'POST',
      body: JSON.stringify(product),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    }
    if (response.ok) {
      setName('');
      setCategory('');
      setSupplier('');
      setDescription('');
      setQuantity('');
      setReOrderLevel('');
      setPrice('');
      setError(null);
      console.log('new product added', json);
      dispatch({ type: 'CREATE_PRODUCT', payload: json });

      // Redirect to the home page
      navigate('/');
    }
  };

  return (
    <div className='form-container'>
      <form className="format" onSubmit={handleSubmit}>
        <h3>Add a new Product</h3>

        <label>Product Name :</label>
        <input
          type='text'
          onChange={(e) => setName(e.target.value)}
          value={name}
        />

        <label>Category :</label>
        <input
          type='text'
          onChange={(e) => setCategory(e.target.value)}
          value={category}
        />

        <label>Supplier :</label>
        <input
          type='text'
          onChange={(e) => setSupplier(e.target.value)}
          value={supplier}
        />

        <label>Description :</label>
        <input
          type='text'
          onChange={(e) => setDescription(e.target.value)}
          value={description}
        />

        <label>Quantity:</label>
        <input
          type='number'
          onChange={(e) => setQuantity(e.target.value)}
          value={quantity}
        />

        <label>Re-Order Level:</label>
        <input
          type='number'
          onChange={(e) => setReOrderLevel(e.target.value)}
          value={reOrderLevel}
        />

        <label>Price:</label>
        <input
          type='number'
          onChange={(e) => setPrice(e.target.value)}
          value={price}
        />

        <button>Add Product</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default ProductForm;
