import { useProductsContext } from "../hooks/useProductsContext";
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

//components
import ProductDetails from "../components/ProductDetails"

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState(""); // State to store search term
  const [selectedSupplier, setSelectedSupplier] = useState(""); // State to store selected supplier
  const [filteredProducts, setFilteredProducts] = useState([]); // State to store filtered products

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products');
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_PRODUCTS', payload: json });
        setFilteredProducts(json); // Initialize the filterProduct with the products
      }
    };

    fetchProducts();
  }, [dispatch]);

  useEffect(() => {
    if (products === null) {
      // If products is null
      return;
    }

    // Filter products based on the search term and selected supplier when they change
    const filtered = products.filter((product) => {
      const matchesSearch = Object.values(product).some((field) => {
        if (typeof field === 'string') {
          return field.toLowerCase().includes(searchTerm.toLowerCase());
        }
        return false;
      });

      const matchesSupplier = selectedSupplier === "" || product.supplier === selectedSupplier;

      return matchesSearch && matchesSupplier;
    });

    setFilteredProducts(filtered);
  }, [searchTerm, selectedSupplier, products]);

  // Create an array of unique suppliers for the dropdown
  const suppliers = [...new Set(products.map((product) => product.supplier))];

  return (
    <div className="home">
      <div className="top-bar">
        <Link to="/NewProduct">
          <button className="createb">Add</button>
        </Link>

        <div className="search-bar">
          <span className="search-icon">search</span>
          <input
            type="text"
            placeholder="Search Products"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <div className="filter-bar">
          <span>Filter by Supplier:</span>
          <select
            value={selectedSupplier}
            onChange={(e) => setSelectedSupplier(e.target.value)}
          >
            <option value="">All Suppliers</option>
            {suppliers.map((supplier) => (
              <option key={supplier} value={supplier}>
                {supplier}
              </option>
            ))}
          </select>
        </div>
      </div>

      <table className="product-table">
        {/* ...Table headers */}
        <tbody>
          {filteredProducts && filteredProducts.map((product) => (
            <ProductDetails key={product._id} product={product} />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
