import { useProductsContext } from "../hooks/useProductsContext";
import { useEffect, useState } from "react";
import { Link } from 'react-router-dom';

//components
import ProductDetails from "../components/ProductDetails"

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState(""); // State to store search term
  const [filteredProducts, setFilteredProducts] = useState([]); // State to store filtered products

  const [selectedSupplier, setSelectedSupplier] = useState("");


  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('/api/products');
      const json = await response.json();

      if (response.ok) {
        dispatch({ type: 'SET_PRODUCTS', payload: json });
        setFilteredProducts(json); // initiallize the filterProduct with the products
      }
    };

    fetchProducts();
  }, [dispatch]);

  useEffect(() => {

    if (products === null) {
        // If products is null
        return;
      }
    // Filter products based on the search term when it changes
    const filtered = products.filter((product) =>
      Object.values(product).some((field) => {
        if (typeof field === 'string') {
          return field.toLowerCase().includes(searchTerm.toLowerCase());
        }
        return false;
      })
    );

    setFilteredProducts(filtered);
  }, [searchTerm, products]);

  return (
    <div className="home">
        <div className="top-bar">
            <Link to="/NewProduct">
            <button className="createb">Add</button>
            </Link>
            <div className="filter-controls">
  {/* Other filters go here */}

  <label>Supplier:</label>
  <div className="dropdown">
    <select
      value={selectedSupplier}
      onChange={(e) => setSelectedSupplier(e.target.value)}
    >
      <option value="">All Suppliers</option>
      {suppliers.map((supplier) => (
        <option key={supplier} value={supplier}>
          {supplier}
        </option>
      ))}
    </select>
  </div>
</div>

            <div className="search-bar">
                <span className="search-icon">search</span>
                <input
                    type="text"
                    placeholder="Search Products"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
        </div>

        <table className="product-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Supplier</th>
              <th>Description</th>
              <th>Quantity</th>
              <th>Re-OrderLevel</th>
              <th>Price</th>
              <th>Created</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredProducts && filteredProducts.map((product) => (
              <ProductDetails key={product._id} product={product} />
            ))}
          </tbody>
        </table>
    </div>
  );
}

export default Home;
