// export default UpdatePage;
import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const UpdatePage = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  //error checking
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  // State to store product details
  const [product, setProduct] = useState({
    name: '',
    category: '',
    supplier: '',
    description: '',
    quantity: '',
    reOrderLevel: '',
    price: '',
  });

  // State for form fields
  const [formData, setFormData] = useState({
    name: '',
    category: '',
    supplier: '',
    description: '',
    quantity: '',
    reOrderLevel: '',
    price: '',
  });

  useEffect(() => {
    // Fetch product details based on 'id' and set them in 'product' state
    fetch(`http://localhost:3000/api/products/${id}`)

      .then((response) => response.json())
      .then((data) => {
        setProduct(data);
        // Also set form data to initial product details
        setFormData(data);
      })
      .catch((error) => console.error("Error fetching product details:", error));
  }, [id]);

  // Function to handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    // Update the formData state with the new input value
    setFormData({ ...formData, [name]: value });
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send a PUT request to update the product with the new data
      const response = await fetch(`http://localhost:3000/api/products/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setError(null);
      setEmptyFields([]);
        // Product updated successfully, navigate back to the home page
        navigate('/');
      } else {
        console.error('Failed to update product:', response.statusText);
      }
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  return (
    <div className="update-page">
      <h2>Update Product</h2>
      <form onSubmit={handleSubmit}>
        <label>Product Name:</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleInputChange}
          className={emptyFields.includes('name') ? 'error' : ''}
        />

        <label>Category:</label>
        <input
          type="text"
          name="category"
          value={formData.category}
          onChange={handleInputChange}
          className={emptyFields.includes('category') ? 'error' : ''}
        />

        <label>Supplier:</label>
        <input
          type="text"
          name="supplier"
          value={formData.supplier}
          onChange={handleInputChange}
          className={emptyFields.includes('supplier') ? 'error' : ''}
        />

        <label>Description:</label>
        <input
          type="text"
          name="description"
          value={formData.description}
          onChange={handleInputChange}
          className={emptyFields.includes('description') ? 'error' : ''}
        />

        <label>Quantity:</label>
        <input
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleInputChange}
          className={emptyFields.includes('quantity') ? 'error' : ''}
        />

        <label>Re-Order Level:</label>
        <input
          type="number"
          name="reOrderLevel"
          value={formData.reOrderLevel}
          onChange={handleInputChange}
          className={emptyFields.includes('reOrderLevel') ? 'error' : ''}
        />

        <label>Price:</label>
        <input
          type="number"
          name="price"
          value={formData.price}
          onChange={handleInputChange}
          className={emptyFields.includes('price') ? 'error' : ''}
        />

        <button type="submit">Update Product</button>
      </form>
    </div>
  );
};

export default UpdatePage;
