import { useState } from "react";
import { useProductsContext } from "../hooks/useProductsContext";
import { useNavigate } from "react-router-dom";

// import { useEffect } from 'react';

const ProductForm = () => {
  const { dispatch } = useProductsContext();
  const navigate = useNavigate(); // Hook for programmatic navigation

  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
  const [supplier, setSupplier] = useState("");
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState("");
  const [reOrderLevel, setReOrderLevel] = useState("");
  const [price, setPrice] = useState("");

  //for error checking
  const [error, setError] = useState([]);
  const [emptyFields, setEmptyFields] = useState([]);

  // const [suppliersList, setSuppliersList] = useState([]);

  // useEffect(() => {
  //   // Fetch the list of suppliers from your API
  //   const fetchSuppliers = async () => {
  //     try {
  //       const response = await fetch('/api/suppliers');                      //when supplier data is added
  //       const data = await response.json();

  //       if (response.ok) {
  //         // Extract the list of supplier names
  //         const supplierNames = data.map((supplier) => supplier.name);
  //         setSuppliersList(supplierNames);
  //       } else {
  //         console.error('Failed to fetch suppliers');
  //       }
  //     } catch (error) {
  //       console.error('Error fetching suppliers:', error);
  //     }
  //   };

  //   fetchSuppliers();
  // }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!/^[a-zA-Z][a-zA-Z0-9\s]*$/.test(name)) {
      setError(
        "Name must start with a letter and can only contain letters, numbers, and spaces."
      );
      return;
    }

    if (!/^[a-zA-Z][a-zA-Z0-9\s]*$/.test(description)) {
      setError(
        "description must start with a letter and can only contain letters, numbers, and spaces"
      );
      return;
    }

    const quantityVal = parseInt(quantity);
    if (quantityVal <= 0) {
      setError("Quantity must be a valid number greater than 0");
      return;
    }
    const reOrderLevelValue = parseFloat(reOrderLevel);
    if (reOrderLevelValue <= 0 || reOrderLevelValue >= parseFloat(quantity)) {
      setError(
        "Re-Order Level must be a valid number greater than 0 and less than Quantity."
      );
      return;
    }
    const priceVal = parseFloat(price);
    if (priceVal <= 0) {
      setError("Price must be a valid number greater than 0");
      return;
    }

    const product = {
      name,
      category,
      supplier,
      description,
      quantity,
      reOrderLevel,
      price,
    };

    const response = await fetch("/api/products", {
      method: "POST",
      body: JSON.stringify(product),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setName("");
      setCategory("");
      setSupplier("");
      setDescription("");
      setQuantity("");
      setReOrderLevel("");
      setPrice("");
      setError(null);
      setEmptyFields([]);
      console.log("new product added", json);
      dispatch({ type: "CREATE_PRODUCT", payload: json });

      // Redirect to the home page
      navigate("/");
    }
  };

  return (
    <div className="form-container">
      <form className="format" onSubmit={handleSubmit}>
        <h3>Add a new Product</h3>

        <label>Product Name :</label>
        <input
          type="text"
          onChange={(e) => setName(e.target.value)}
          value={name}
          className={emptyFields.includes("name") ? "error" : ""}
        />
        {error && error.includes("name") && (
          <span className="error">{error}</span>
        )}

        <label>Category :</label>
        <input
          type="text"
          onChange={(e) => setCategory(e.target.value)}
          value={category}
          className={emptyFields.includes("category") ? "error" : ""}
        />

        <label>Supplier :</label>
        <input
          type="text"
          onChange={(e) => setSupplier(e.target.value)}
          value={supplier}
          className={emptyFields.includes("supplier") ? "error" : ""}
        />
        {/* 
        <label>Supplier:</label>
        <select
          value={supplier}
          onChange={(e) => setSupplier(e.target.value)}
          className={emptyFields.includes('supplier') ? 'error' : ''}
        >
          <option value="">Select Supplier</option>
          {suppliersList.map((supplierName) => (
            <option key={supplierName} value={supplierName}>
              {supplierName}
            </option>
          ))}
        </select> */}

        <label>Description :</label>
        <input
          type="text"
          onChange={(e) => setDescription(e.target.value)}
          value={description}
          className={emptyFields.includes("description") ? "error" : ""}
        />
        {error && error.includes("description") && (
          <span className="error">{error}</span>
        )}

        <label>Quantity:</label>
        <input
          type="number"
          onChange={(e) => setQuantity(e.target.value)}
          value={quantity}
          className={
            emptyFields.includes("quantity") ||
            (error && error.includes("quantity"))
              ? "error"
              : ""
          }
        />
        {emptyFields.includes("quantity") && (
          <span className="error">Quantity Level is required.</span>
        )}
        {error && error.includes("quantity") && (
          <span className="error">{error}</span>
        )}

        <label>Re-OrderLevel:</label>
        <input
          type="number"
          onChange={(e) => setReOrderLevel(e.target.value)}
          value={reOrderLevel}
          className={
            emptyFields.includes("reOrderLevel") ||
            (error && error.includes("reOrderLevel"))
              ? "error"
              : ""
          }
        />
        {error && error.includes("reOrderLevel") && (
          <span className="error">{error}</span>
        )}

        <label>Price:</label>
        <input
          type="number"
          onChange={(e) => setPrice(e.target.value)}
          value={price}
          className={emptyFields.includes("price") ? "error" : ""}
        />

        <button>Add Product</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default ProductForm;
