import React, { useState } from 'react';

// ...

const Home = () => {
  const { products, dispatch } = useProductsContext();
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [supplierFilter, setSupplierFilter] = useState('');
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    // Fetch products and set initial filter state...
  }, [dispatch]);

  useEffect(() => {
    // Apply filters when search term, category filter, or supplier filter changes...
    if (products === null) {
      return;
    }

    const filtered = products.filter((product) => {
      // Filter by search term
      const matchesSearch = Object.values(product).some((field) => {
        if (typeof field === 'string') {
          return field.toLowerCase().includes(searchTerm.toLowerCase());
        }
        return false;
      });

      // Filter by category
      const matchesCategory =
        categoryFilter === '' || product.category.toLowerCase() === categoryFilter.toLowerCase();

      // Filter by supplier
      const matchesSupplier =
        supplierFilter === '' || product.supplier.toLowerCase() === supplierFilter.toLowerCase();

      return matchesSearch && matchesCategory && matchesSupplier;
    });

    setFilteredProducts(filtered);
  }, [searchTerm, products, categoryFilter, supplierFilter]);

  return (
    <div className="home">
      <div className="top-bar">
        {/* Add filter controls */}
        <div className="filter-controls">
          <input
            type="text"
            placeholder="Search Products"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <select
            value={categoryFilter}
            onChange={(e) => setCategoryFilter(e.target.value)}
          >
            <option value="">All Categories</option>
            {/* Map and render unique categories from products */}
            {Array.from(new Set(products?.map((product) => product.category))).map(
              (category) => (
                <option key={category} value={category}>
                  {category}
                </option>
              )
            )}
          </select>
          <select
            value={supplierFilter}
            onChange={(e) => setSupplierFilter(e.target.value)}
          >
            <option value="">All Suppliers</option>
            {/* Map and render unique suppliers from products */}
            {Array.from(new Set(products?.map((product) => product.supplier))).map(
              (supplier) => (
                <option key={supplier} value={supplier}>
                  {supplier}
                </option>
              )
            )}
          </select>
        </div>
        <Link to="/NewProduct">
          <button className="createb">Add</button>
        </Link>
      </div>

      <table className="product-table">
        {/* ... Product table header ... */}
        <tbody>
          {filteredProducts && filteredProducts.map((product) => (
            <ProductDetails key={product._id} product={product} />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Home;
