import { useState } from 'react';
import { useProductsContext } from '../hooks/useProductsContext';
import { useNavigate } from 'react-router-dom';
// import { useEffect } from 'react';

const ProductForm = () => {
  const { dispatch } = useProductsContext();
  const navigate = useNavigate(); // Hook for programmatic navigation

  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [supplier, setSupplier] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [reOrderLevel, setReOrderLevel] = useState('');
  const [price, setPrice] = useState('');


  //for error checking 
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  // const [suppliersList, setSuppliersList] = useState([]);

  // useEffect(() => {
  //   // Fetch the list of suppliers from your API
  //   const fetchSuppliers = async () => {
  //     try {
  //       const response = await fetch('/api/suppliers');                      //when supplier data is added 
  //       const data = await response.json();

  //       if (response.ok) {
  //         // Extract the list of supplier names
  //         const supplierNames = data.map((supplier) => supplier.name);
  //         setSuppliersList(supplierNames);
  //       } else {
  //         console.error('Failed to fetch suppliers');
  //       }
  //     } catch (error) {
  //       console.error('Error fetching suppliers:', error);
  //     }
  //   };

  //   fetchSuppliers();
  // }, []);

  const validateForm = () => {
    const newErrors = {};

    if (!name) {
      newErrors.name = 'Product Name is required';
    }

    if (!category) {
      newErrors.category = 'Category is required';
    }

    if (!supplier) {
      newErrors.supplier = 'Supplier is required';
    }

    if (!description) {
      newErrors.description = 'Description is required';
    }

    if (!quantity || parseInt(quantity) <= 0) {
      newErrors.quantity = 'Quantity must be greater than 0';
    }

    if (!reOrderLevel || parseInt(reOrderLevel) <= 0) {
      newErrors.reOrderLevel = 'Re-Order Level must be greater than 0';
    }

    if (!price) {
      newErrors.price = 'Price is required';
    }

    setError(newErrors);

    return Object.keys(newErrors).length === 0; // Return true if there are no errors
  };





  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      // Don't proceed with form submission if there are errors
      return;
    }

    const product = { name, category, supplier, description, quantity, reOrderLevel, price };

    const response = await fetch('/api/products', {
      method: 'POST',
      body: JSON.stringify(product),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setName('');
      setCategory('');
      setSupplier('');
      setDescription('');
      setQuantity('');
      setReOrderLevel('');
      setPrice('');
      setError(null);
      setEmptyFields([]);
      console.log('new product added', json);
      dispatch({ type: 'CREATE_PRODUCT', payload: json });

      // Redirect to the home page
      navigate('/');
    }
  }


  return (
    <div className='form-container'>
      <form className="format" onSubmit={handleSubmit}>
        <h3>Add a new Product</h3>

        <label>Product Name :</label>
        <input
          type='text'
          onChange={(e) => setName(e.target.value)}
          value={name}
          className={emptyFields.includes('name') ? 'error' : ''}
        />

        <label>Category :</label>
        <input
          type='text'
          onChange={(e) => setCategory(e.target.value)}
          value={category}
          className={emptyFields.includes('category') ? 'error' : ''}

        />

        <label>Supplier :</label>
        <input
          type='text'
          onChange={(e) => setSupplier(e.target.value)}
          value={supplier}
          className={emptyFields.includes('supplier') ? 'error' : ''}
        />
{/* 
        <label>Supplier:</label>
        <select
          value={supplier}
          onChange={(e) => setSupplier(e.target.value)}
          className={emptyFields.includes('supplier') ? 'error' : ''}
        >
          <option value="">Select Supplier</option>
          {suppliersList.map((supplierName) => (
            <option key={supplierName} value={supplierName}>
              {supplierName}
            </option>
          ))}
        </select> */}

        <label>Description :</label>
        <input
          type='text'
          onChange={(e) => setDescription(e.target.value)}
          value={description}
          className={emptyFields.includes('description') ? 'error' : ''}
        />

        <label>Quantity:</label>
        <input
          type='number'
          onChange={(e) => setQuantity(e.target.value)}
          value={quantity}
          className={emptyFields.includes('quantity') ? 'error' : ''}
        />

        <label>Re-OrderLevel:</label>
        <input
          type='number'
          onChange={(e) => setReOrderLevel(e.target.value)}
          value={reOrderLevel}
          className={emptyFields.includes('reOrderLevel') ? 'error' : ''}
        />

        <label>Price:</label>
        <input
          type='number'
          onChange={(e) => setPrice(e.target.value)}
          value={price}
          className={emptyFields.includes('price') ? 'error' : ''}
        />

        <button>Add Product</button>
        {error && <div className="error">{error}</div>}
      </form>





    </div>



  )
}

export default ProductForm
