import React from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { formatDistanceToNow } from 'date-fns';

const ExportToPDF = ({ data }) => {
  const exportToPDF = () => {
    const doc = new jsPDF();

    // Define the columns and rows of the table
    const columns = ['ID', 'Product Name', 'Category', 'Supplier', 'Description', 'Quantity', 'Re-Order Level', 'Price', 'Created'];
    const rows = data.map((product, index) => [
      product._id,
      product.name,
      product.category,
      product.supplier,
      product.description,
      product.quantity,
      product.reOrderLevel,
      `Rs ${product.price}`,
      formatDistanceToNow(new Date(product.createdAt), { addSuffix: true }),
    ]);

    // Set the table position and size
    const margin = 10;
    const pageWidth = doc.internal.pageSize.width - 2 * margin;
    doc.setFontSize(12);
    doc.text('Product Table', margin, 15);

    const tableStyles = {
      theme: 'striped',
      headStyles: { fillColor: [41, 128, 185], textColor: 255 },
      columnStyles: { 0: { cellWidth: 30 } },
      columnLineWidth: 0.1, // Specify the width of the lines between cells
    };

    const columnStyles = {};
    columns.forEach((column, index) => {
      columnStyles[index] = { lineWidth: 0.1 }; // Specify the width of the lines between columns
    });

    doc.autoTable({
      startY: 20,
      head: [columns],
      body: rows,
      ...tableStyles,
      ...columnStyles,
    });

    // Save the PDF with a name
    doc.save('product_List.pdf');
  };

  return (
    <div className='downloadb'>
      <button onClick={exportToPDF}>download</button>
    </div>
  );
};

export default ExportToPDF;
