import { useProductsContext } from '../hooks/useProductsContext'


//date fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const ProductDetails = ({ product }) => {
    const { dispatch } = useProductsContext()

    const handleClickDelete = async() =>{
        const response = await fetch('/api/products/'+ product._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if(response.ok){
            dispatch({type: 'DELETE_PRODUCT' , payload: json})
        }
    }
    const handleClickUpdate = async () => {
        const updatedProduct = {
            // Include the fields you want to update and their new values here
            // For example:
            // title: newTitle,
            // load: newLoad,
            // reps: newReps,

            category: 'trying to update',
        };
        const response = await fetch('/api/products/' + product._id, {
            method: 'PUT', // You can use PUT or PATCH depending on your API's design.
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedProduct),
        });

        const json = await response.json();
        if (response.ok) {
            dispatch({ type: 'UPDATE_PRODUCT', payload: json });
        }
    };

    return(
        // <div className="product-details">
        //     <h4>{product.name}</h4>
        //     <p><strong>category : </strong>{product.category} </p>
        //     <p><strong>supplier: </strong>{product.supplier} </p>
        //     <p><strong>description: </strong>{product.description} </p>
        //     <p><strong>quantity: </strong>{product.quantity} </p>
        //     <p><strong>price: </strong>{product.price} </p>

        //     <p>{formatDistanceToNow(new Date(product.createdAt),{ addSuffix: true})}</p>
        //     <span className="deleteb material-symbols-outlined" onClick = {handleClickDelete} >delete</span>
        //     <span className="editb material-symbols-outlined" onClick= {handleClickUpdate} >edit</span>
        // </div>
    <div className="home">
      <div className='product-table-wrapper'>
      <table className="product-table">
        <thead classname = "product-table-header">
          <tr>
          </tr>
        </thead>
        <tbody>
            <tr key={product._id}>
              <td>{product.name}</td>
              <td>{product.category}</td>
              <td>{product.supplier}</td>
              <td>{product.description}</td>
              <td>{product.quantity}</td>
              <td>{product.price}</td>
              <td>{formatDistanceToNow(new Date(product.createdAt),{ addSuffix: true})}</td>
              <td><span className="deleteb" onClick={() => handleClickDelete(product)}>delete</span></td>
              <td><span className="editb" onClick={() => handleClickUpdate(product)}>edit</span></td>
            </tr>
        </tbody>
      </table>
      </div>
    </div>

    )
} 


export default ProductDetails
