import { useNavigate } from 'react-router-dom'
import { useProductsContext } from '../hooks/useProductsContext'


//date fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const ProductDetails = ({ product }) => {
    const navigate = useNavigate();
    const { dispatch } = useProductsContext()

    const handleClickDelete = async() =>{
        const response = await fetch('/api/products/'+ product._id, {
            method: 'DELETE'
        })
        const json = await response.json()

        if(response.ok){
            dispatch({type: 'DELETE_PRODUCT' , payload: json})
        }
    }


    return(
          <tr key={product._id}>
            <td>{product.name}</td>
            <td>{product.category}</td>
            <td>{product.supplier}</td>
            <td>{product.description}</td>
            <td>{product.quantity}</td>
            <td>{product.reOrderLevel}</td>
            <td>{product.price}</td>
            <td>{formatDistanceToNow(new Date(product.createdAt),{ addSuffix: true})}</td>
            <td>
                <span className="deleteb" onClick={() => handleClickDelete(product)}>delete</span>

                    <span className="editb" onClick={navigate(`/UpdatePage/${product._id}`)}>edit</span>
            </td>
          </tr>
    )
} 


export default ProductDetails
