    <h2>Add a new Product</h2>

    <div className="form-row">
      <div className="form-column">
        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Product Name:</label>
        <input
          type="text"
          onChange={(e) => setName(e.target.value)}
          value={name}
          className={
            emptyFields.includes("name") || (error && error.name)
              ? "error"
              : ""
          }
        />
        {error.name && <span className="error">{error.name}</span>}

        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Supplier:</label>
        <input
          type="text"
          onChange={(e) => setSupplier(e.target.value)}
          value={supplier}
          className={
            emptyFields.includes("supplier") || (error && error.supplier)
              ? "error"
              : ""
          }
          />
        {error.supplier && <span className="error">{error.supplier}</span>}
      </div>

      <div className="form-column">
        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Category:</label>
        <input
          type="text"
          onChange={(e) => setCategory(e.target.value)}
          value={category}
          className={
            emptyFields.includes("category") || (error && error.category)
              ? "error"
              : ""
          }
          />
        {error.category && <span className="error">{error.category}</span>}

        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Description:</label>
        <input
          type="text"
          onChange={(e) => setDescription(e.target.value)}
          value={description}
          className={
            emptyFields.includes("description") || (error && error.description)
              ? "error"
              : ""
          }
          />
        {error.description && <span className="error">{error.description}</span>}
      </div>
    </div>

    <div className="form-row">
      <div className="form-column">
        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Quantity:</label>
        <input
          type="number"
          onChange={(e) => setQuantity(e.target.value)}
          value={quantity}
          className={
            emptyFields.includes("quantity") || (error && error.quantity)
              ? "error"
              : ""
          }
          />
        {error.quantity && <span className="error">{error.quantity}</span>}
      </div>

      <div className="form-column">
        <label class="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2"
              for="grid-first-name">Re-Order Level:</label>
